  let [modal, setModal]  = useState(false); /* UI의 현재 상태를 state로 저장, 형식은 자유 */

  //map 함수
  // array 자료 갯수 만큼 함수안의 코드 실행해줌
  // 함수의 파라미터는 array 안에 있던 자료임
  // return에 뭐 적으면 array로 담아줌
