          return (
            <div className="list" key={i}> 
              <h4 onClick={()=>{ setModal(!modal) }}>{ 글제목[i] } <span onClick={ ()=> { 따봉변경(따봉[i] + 1)} }>👍</span> { 따봉[i] }</h4>
