         modal == true ? <Modal/> : null     
        }

    </div>
  );
}


function Modal() {
  return(
    <div className="modal">
    <h4>제목</h4>
    <p>날짜</p>
    <p>상세내용</p>
   </div>
  )
};

export default App;
